	function SigWebEvent() {
		var OldEvStatus = EvStatus;

		var xhr = SigWebcreateXHR();

		if (xhr) {
			xhr.open("GET", baseUri + "EventStatus", true);
			xhr.onload = function () {
				EvStatus = xhr.responseText
				if ((OldEvStatus & 0x01) && (EvStatus & 0x02)) {
					if (onSigPenDown) {
						onSigPenDown();
					}
				}

				if ((OldEvStatus & 0x02) && (EvStatus & 0x01)) {
					if (onSigPenUp) {
						onSigPenUp();
					}
				}
			}
			xhr.send(null);
		}
	}


